mapper.select:
select 

from problem_option
where id = #{id,jdbcType=VARCHAR}
  and problem_id = #{problemId,jdbcType=VARCHAR}

mapper.delete:
delete from problem_option
where id = #{id,jdbcType=VARCHAR}
  and problem_id = #{problemId,jdbcType=VARCHAR}

mapper.update:
update problem_option


    line_title = #{lineTitle,jdbcType=VARCHAR},


    option_word = #{optionWord,jdbcType=VARCHAR},


    option_grade = #{optionGrade,jdbcType=INTEGER},


where id = #{id,jdbcType=VARCHAR}
  and problem_id = #{problemId,jdbcType=VARCHAR}

update problem_option
set line_title = #{lineTitle,jdbcType=VARCHAR},
  option_word = #{optionWord,jdbcType=VARCHAR},
  option_grade = #{optionGrade,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}
  and problem_id = #{problemId,jdbcType=VARCHAR}
